/*************************************************
 * Simple program that takes the gputemps.txt    *
 * file generated by my gmon bash script and     *
 * outputs the relevant GPU temp information     *
 * in a neat and concise format. gmon simply     *
 * runs "nvidia-smi" every two seconds to gather *
 * information and store it in the above .txt    *
 ************************************************/

import java.io.*;
import java.util.Arrays;

public class Main {

    public static void main(String[] args) throws IOException {
        ValueExtract ExtractTemps = new ValueExtract("Temps");
        int[] temps = ExtractTemps.GetValues(); Arrays.sort(temps);
        FormatDisplay displayTempStats = new FormatDisplay(temps, "Temps (Celsius)");

        ValueExtract ExtractFans = new ValueExtract("Fan Speed");
        int [] fans = ExtractFans.GetValues(); Arrays.sort(fans);
        FormatDisplay displayFanStats = new FormatDisplay(fans, "Fan Speed (%)");
    }
}